sp_widget.client_script:
api.controller=function() {
  /* widget controller */
	var c = this;
	var currentBodyList = 0;
	$rootScope.$on('noteUpdated', function(event, data){
		//c.server.refresh();
		console.log(currentBodyList);
		console.log("data on noteUpdated",data);
		c.data.notes[currentBodyList].title = data.title;
		c.data.notes[currentBodyList].note = data.note;
	});

	c.selectItem = function(index){
		currentBodyList = index;
		var id = c.data.notes[index].sys_id;
		var title = c.data.notes[index].title;
		var note = c.data.notes[index].note;
		console.log("Note ID: " + id);
		$rootScope.noteID = id;
		var noteObj = {};
		noteObj.noteID = id;
		noteObj.title = title;
		noteObj.note = note;
		$rootScope.$emit('selectNote', noteObj);
	}
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	//create an array to populate with notes
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}
})();
